    },
    body: JSON.stringify({
        'model': 'text-davinci-edit-001',
        'input': `${text}`,
        'instruction': `${textTwo}`
    })
  })
  .then(response => response.json())
  .then(data => {
    console.log(data);
    setCodex(data)
    setIsLoading(false);
    console.log(codex + "codex")
  })
  return codex;
}

