import logo from './logo.svg';
import './App.css';
import TextField from '@mui/material/TextField'
import Button from '@mui/material/Button';
import { useState } from 'react';
import React from 'react';

function App() {

  // create a usestate var called test that captures the value of the input wtext
  const [text, setText] = React.useState([]);
  const [codex, setCodex] = React.useState([]);


  // update state value for input
  const onChangeL = (event) => {
      setText(event);
      console.log(event);
    };

  let submitCodexCall = () => {
    fetch('https://api.openai.com/v1/edits', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer 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'
    },
    body: JSON.stringify({
        'model': 'text-davinci-edit-001',
        'input': `${text}`,
        'instruction': 'Fix the spelling mistakes'
    })
  })
  .then(response => response.json())
  .then(data => {
    console.log(data);
    setCodex(data)
    console.log(codex + "codex")
  })
}

  return (
    <div className="paragraph-text">
    Text Sample<TextField value={text} name="text" onChange={e => onChangeL(e.target.value)} />
    <Button onClick={submitCodexCall} className='submit-one'>Primary</Button>
    <br></br>
    {/* <br></br>
    Instructions & Directions<TextField value={firstName}   name="firstName" onChange={e => setFirstName(e.target.value)} />
    <Button className='submit-two'>Primary</Button> */}
    </div>
  );
}

export default App;
